mapper.select:
SELECT A.* FROM( SELECT PROD_ID, PROD_NAME, TO_CHAR(PROD_INSDATE, 'YYYY-MM-DD') PROD_INSDATE , PROD_COST, PROD_PRICE, PROD_MILEAGE , PROD_BUYER , BUYER_NAME , PROD_LGU, LPROD_NM , ROWNUM RNUM
) A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE PROD SET PROD_NAME = #{prod_name}, PROD_COST = #{prod_cost}, PROD_PRICE = #{prod_price}, PROD_SALE = #{prod_sale}, PROD_OUTLINE = #{prod_outline}, PROD_DETAIL = #{prod_detail,jdbcType=VARCHAR}, PROD_TOTALSTOCK = #{prod_totalstock}, PROD_PROPERSTOCK = #{prod_properstock}, PROD_SIZE = #{prod_size,jdbcType=VARCHAR}, PROD_COLOR = #{prod_color,jdbcType=VARCHAR}, PROD_DELIVERY = #{prod_delivery,jdbcType=VARCHAR}, PROD_UNIT = #{prod_unit,jdbcType=VARCHAR}, PROD_QTYIN = #{prod_qtyin,jdbcType=NUMERIC}, PROD_QTYSALE = #{prod_qtysale,jdbcType=NUMERIC}, PROD_MILEAGE = #{prod_mileage,jdbcType=NUMERIC}
, PROD_IMG = #{prod_img}
WHERE PROD_ID = #{prod_id}
